// script.js
let cards = [];
let flippedCards = [];
let matchedCards = [];
let score = 0;
let timeLeft = 60; // 60 seconds countdown timer
let timerInterval;
let isGameOver = false;

const cardImages = [
    'data:image/jpeg;base64,', 
    'data:image/jpeg;base64,', 
    'data:image/jpeg;base64,', 
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmJpqUyNr5m7Cclll1Hai_b9drYWfHaS1Vug&s',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSEMCAsmpBVAIpzAHfHBDmf3CSse-iF6vMuSQ&s',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHqo2X7WCTeUUFL-Iu2SE9fQ7Vv0w7naqFPA&s',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTxLCMSixFQosBNG-iDlxk8oL4fz2tUEbDeOg&s',
     'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTo2BhOybwF7X70sqH-LaCk72TgNnt2SabgJA&s'
]; // Example images (use your own images for the game)
let gameBoard = document.getElementById("board");
let scoreCount = document.getElementById("score-count");
let timeCount = document.getElementById("time-count");

function startGame() {
    // Reset variables
    cards = [];
    flippedCards = [];
    matchedCards = [];
    score = 0;
    isGameOver = false;
    timeLeft = 60; // Reset the timer to 60 seconds
    scoreCount.textContent = score;
    timeCount.textContent = timeLeft;

    // Shuffle card images and duplicate them
    let shuffledCards = [...cardImages, ...cardImages].sort(() => Math.random() - 0.5);

    // Clear board and generate new cards
    gameBoard.innerHTML = '';
    shuffledCards.forEach(image => {
        let card = document.createElement("div");
        card.classList.add("card");
        card.setAttribute("data-image", image);
        card.addEventListener("click", flipCard);
        cards.push(card);
        gameBoard.appendChild(card);
    });

    // Start countdown timer
    if (timerInterval) clearInterval(timerInterval); // Clear any existing interval
    timerInterval = setInterval(updateTimer, 1000);
}

function updateTimer() {
    if (timeLeft <= 0) {
        clearInterval(timerInterval);
        endGame();
        return;
    }
    timeLeft--;
    timeCount.textContent = timeLeft;
}

function flipCard() {
    if (isGameOver || this.classList.contains("flipped") || flippedCards.length === 2) return;

    this.classList.add("flipped");
    this.style.backgroundImage = `url(${this.getAttribute("data-image")})`;
    flippedCards.push(this);

    if (flippedCards.length === 2) {
        checkMatch();
    }
}

function checkMatch() {
    let [firstCard, secondCard] = flippedCards;

    if (firstCard.getAttribute("data-image") === secondCard.getAttribute("data-image")) {
        firstCard.classList.add("matched");
        secondCard.classList.add("matched");
        matchedCards.push(firstCard, secondCard);
        flippedCards = [];
        score++;
        scoreCount.textContent = score;

        if (matchedCards.length === cards.length) {
            isGameOver = true;
            clearInterval(timerInterval);
            setTimeout(() => alert("You win! Final Score: " + score), 500);
        }
    } else {
        setTimeout(() => {
            firstCard.classList.remove("flipped");
            secondCard.classList.remove("flipped");
            firstCard.style.backgroundImage = '';
            secondCard.style.backgroundImage = '';
            flippedCards = [];
        }, 1000);
    }
}

function endGame() {
    isGameOver = true;
    alert("Game Over! Final Score: " + score);
}

startGame(); // Initialize the game when the page loads
